mapper.update:
UPDATE play_list
SET pl_name = #{pl_name},
    pl_comment = #{pl_comment}
WHERE pl_id = #{pl_id}

mapper.delete:
DELETE
FROM play_list_good
where pl_id = #{pl_id} and u_id = #{u_id}

mapper.select:
SELECT pl_id
FROM play_list_good
WHERE u_id = #{u_id}

SELECT pl_id
FROM play_list
WHERE u_id = #{u_id}

mapper.update:
update play_list_contents
set pl_id = #{pl_id}
where plc_id = #{plc_id}

UPDATE vod_history
SET vh_comment = #{vh_comment}
WHERE vh_id = (SELECT vh_id
               FROM play_list_contents
               WHERE plc_id = #{plc_id});
